package ejercicios;

public class ejemplo3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n1=2, n2;
		
	
		n2=n1 * n1; // n2=4
		System.out.println("n2 vale :"+n2);
		
		n2=n2-n1; // n2=2
		System.out.println("n2 vale :"+n2);
		
		n2=n2+n1+15; // n2=19
		System.out.println("n2 vale :"+n2);
		
		n2=n2/n1; // n2=9
		System.out.println("n2 vale :"+n2);
		
		n2=n2%n1; // n2=1
		System.out.println("n2 vale :"+n2);
	}

}
