package repaso;

public class Ejerprop5 {

	/*Ejercicio 2
	Muestra los números múltiplos de 5 
	de 0 a 100 utilizando un bucle while.*/
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int contar=0;
		
		while(contar<101) {
			System.out.println(contar);
			contar=contar+5;
		}
	
	}

}