package repaso;

public class Ejerprop6 {

	/*
	 * Ejercicio 3 Muestra los números múltiplos de 5 de 0 a 100 utilizando un bucle
	 * do-while.
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int contar = 0;

		do {
			System.out.println(contar);
			contar = contar + 5;
		} while (contar < 101);

	}

}