package repaso;

public class Ejerprop9 {

	/*Ejercicio 6
	Muestra los números del 320 al 160, contando de 
	20 en 20 utilizando un bucle
	do-while.*/
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int contar=320;
		do {
			System.out.println(contar);
			contar=contar-20;
			
		}while( contar>=160);
		
		
	}

}